mapper.select:

        SELECT
	     cart_mer_id,
	 	 number,
   		 total_price,
   		 create_date,
   		 update_date,
   		 spread_account_id,
   		 mer_id,
   		 account_id
	FROM cart_mer_tb


		AND  number=#{number}


		AND  mer_id=#{merId}


		AND  account_id=#{accountId}

	AND ${orderName} 

	>= 


	  <= 

	(SELECT ${orderName} FROM cart_mer_tb 


		AND  number=#{number}


		AND  mer_id=#{merId}


		AND  account_id=#{accountId}


		ORDER BY 
			${orderName}  ${orderWay}
		LIMIT #{pageNum}, 1
		) 

	ORDER BY
		${orderName}  ${orderWay}
	LIMIT
		#{pageSize}


SELECT
 	count(cart_mer_id)
FROM cart_mer_tb


	AND  number=#{number}


	AND  mer_id=#{merId}


	AND  account_id=#{accountId}
