mapper.select:

 SELECT
	     notice_id,
	 	 region,
   		 type,
   		 is_mer_dynamic,
   		 title,
   		 img_address,
   		 content,
   		 status,
   		 create_date,
   		 update_date,
   		 account_id,
   		 business_id
	FROM notice_tb


		AND  region=#{region}


		AND  type=#{type}


		AND  is_mer_dynamic=#{isMerDynamic}


		AND  status=#{status}


		AND account_id=#{accountId}


		AND  business_id=#{businessId}

	AND ${orderName} 

	>= 


	  <= 

	(SELECT ${orderName} FROM notice_tb 


		AND  region=#{region}


		AND  type=#{type}


		AND  is_mer_dynamic=#{isMerDynamic}


		AND  status=#{status}


		AND  account_id=#{accountId}


		AND  business_id=#{businessId}


		ORDER BY 
			${orderName}  ${orderWay}
		LIMIT #{pageNum}, 1
		) 

	ORDER BY
		${orderName}  ${orderWay}
	LIMIT
		#{pageSize}


SELECT
 	count(notice_id)
FROM notice_tb


	AND  region=#{region}


	AND  type=#{type}


	AND  is_mer_dynamic=#{isMerDynamic}


	AND  status=#{status}


	AND account_id=#{accountId}


	AND  business_id=#{businessId}
