mapper.select:

        SELECT
	     order_id,
	 	 order_number,
   		 type,
   		 pay_type,
   		 region,
   		 platform_proportion,
   		 mer_type,
   		 merchant_account_id,
   		 spread_account_id,
   		 account_id,
   		 status,
   		 substatus,
   		 payment_date,
   		 create_date,
   		 update_date
	FROM order_tb


		AND  type=#{type}


		AND  pay_type=#{payType}


		AND  region=#{region}


		AND  mer_type=#{merType}


		AND  merchant_account_id=#{merchantAccountId}


		AND  spread_account_id=#{spreadAccountId}


		AND  account_id=#{accountId}


		AND  status=#{status}


		AND  substatus=#{substatus}


		AND  DATE(create_date)=DATE(#{createDate})


		AND  DATE(update_date)=DATE(#{updateDate})

	AND ${orderName} 

	>= 


	  <= 

	(SELECT ${orderName} FROM order_tb 


		AND  type=#{type}


		AND  pay_type=#{payType}


		AND  region=#{region}


		AND  mer_type=#{merType}


		AND  merchant_account_id=#{merchantAccountId}


		AND  spread_account_id=#{spreadAccountId}


		AND  account_id=#{accountId}


		AND  status=#{status}


		AND  substatus=#{substatus}


		AND  DATE(create_date)=DATE(#{createDate})


		AND  DATE(update_date)=DATE(#{updateDate})


		ORDER BY 
			${orderName}  ${orderWay}
		LIMIT #{pageNum}, 1
		) 

	ORDER BY
		${orderName}  ${orderWay}
	LIMIT
		#{pageSize}


SELECT
 	count(order_id)
FROM order_tb


	AND  type=#{type}


	AND  pay_type=#{payType}


	AND  region=#{region}


	AND  mer_type=#{merType}


	AND  merchant_account_id=#{merchantAccountId}


	AND  spread_account_id=#{spreadAccountId}


	AND  account_id=#{accountId}


	AND  status=#{status}


	AND  substatus=#{substatus}


	AND  DATE(create_date)=DATE(#{createDate})


	AND  DATE(update_date)=DATE(#{updateDate})
