mapper.select:

        SELECT
	     spread_link_id,
	 	 link,
   		 spread_number,
   		 create_date,
   		 mer_id,
   		 account_id
	FROM spread_link_tb


	AND mer_id=#{merId}


	AND account_id=#{accountId}

	AND ${orderName} 

	>= 


	  <= 

	(SELECT ${orderName} FROM spread_link_tb 


	AND mer_id=#{merId}


		AND account_id=#{accountId}


		ORDER BY 
			${orderName}  ${orderWay}
		LIMIT #{pageNum}, 1
		) 

	ORDER BY
		${orderName}  ${orderWay}
	LIMIT
		#{pageSize}


SELECT
 	count(spread_link_id)
FROM spread_link_tb


	AND mer_id=#{merId}


	AND account_id=#{accountId}
